/*!50003 CREATE*/ /*!50017 DEFINER=`root`@`localhost`*/ /*!50003 TRIGGER `before_cart_insert` BEFORE INSERT ON `cart` FOR EACH ROW BEGIN
    DECLARE available_stock INT;
    
    -- Lấy số lượng tồn kho hiện tại của variant được chọn
    SELECT stock_quantity INTO available_stock
    FROM Product_Variants
    WHERE variant_id = NEW.variant_id;
    
    -- Kiểm tra nếu số lượng yêu cầu lớn hơn số lượng tồn kho
    IF NEW.quantity > available_stock THEN
        SIGNAL SQLSTATE '45000'
        SET MESSAGE_TEXT = 'Số lượng sản phẩm trong kho không đủ';
    END IF;
END */;;
DELIMITER ;
