/*!50003 CREATE*/ /*!50017 DEFINER=`root`@`localhost`*/ /*!50003 TRIGGER `before_order_item_insert` BEFORE INSERT ON `order_items` FOR EACH ROW BEGIN
    DECLARE current_stock INT;
    DECLARE err_message VARCHAR(255);

    -- Lấy số lượng tồn kho hiện tại
    SELECT stock_quantity INTO current_stock
    FROM Product_Variants
    WHERE variant_id = NEW.variant_id;

    -- Kiểm tra tồn tại & đủ hàng
    IF current_stock IS NULL THEN
        SET err_message = 'Không tìm thấy biến thể sản phẩm (variant_id không tồn tại)';
        SIGNAL SQLSTATE '45000'
        SET MESSAGE_TEXT = err_message;

    ELSEIF NEW.quantity > current_stock THEN
        SET err_message = CONCAT('Số lượng tồn kho không đủ. Chỉ còn ', current_stock, ' sản phẩm');
        SIGNAL SQLSTATE '45000'
        SET MESSAGE_TEXT = err_message;
    END IF;
END */;;
DELIMITER ;
