/*!50003 CREATE*/ /*!50017 DEFINER=`root`@`localhost`*/ /*!50003 TRIGGER `after_order_item_change` AFTER INSERT ON `order_items` FOR EACH ROW BEGIN
    DECLARE v_total DECIMAL(20,2);
    
    -- Tính tổng giá trị đơn hàng mới
    SELECT SUM(quantity * price_at_time) INTO v_total
    FROM Order_Items
    WHERE order_id = NEW.order_id;
    
    -- Cập nhật tổng tiền trong bảng Orders
    UPDATE Orders
    SET total_amount = v_total
    WHERE order_id = NEW.order_id;
END */;;
DELIMITER ;
