/*!50003 CREATE*/ /*!50017 DEFINER=`root`@`localhost`*/ /*!50003 TRIGGER `after_order_item_delete` AFTER DELETE ON `order_items` FOR EACH ROW BEGIN
    DECLARE v_total DECIMAL(20,2);
    
    -- Tính tổng giá trị đơn hàng sau khi xóa
    SELECT SUM(quantity * price_at_time) INTO v_total
    FROM Order_Items
    WHERE order_id = OLD.order_id;
    
    -- Cập nhật tổng tiền trong bảng Orders
    UPDATE Orders
    SET total_amount = COALESCE(v_total, 0)
    WHERE order_id = OLD.order_id;
