/*!50003 CREATE*/ /*!50017 DEFINER=`root`@`localhost`*/ /*!50003 TRIGGER `trg_auto_update_payment_status` BEFORE UPDATE ON `orders` FOR EACH ROW BEGIN
    -- Nếu đơn hàng chuyển sang 'Delivered'
    IF NEW.status = 'Delivered' THEN
        SET NEW.payment_status = 'Completed';

    -- Nếu đơn hàng bị hủy
    ELSEIF NEW.status = 'Cancelled' THEN
        IF OLD.payment_status = 'Completed' THEN
            SET NEW.payment_status = 'Refunded';
        ELSEIF OLD.payment_status = 'Pending' THEN
            SET NEW.payment_status = 'Failed';
        END IF;
    END IF;
END */;;
DELIMITER ;
