/*!50003 CREATE*/ /*!50017 DEFINER=`root`@`localhost`*/ /*!50003 TRIGGER `update_product_variant_stock_after_cancel` AFTER UPDATE ON `orders` FOR EACH ROW BEGIN
    -- Tất cả khai báo phải nằm đầu BEGIN block
    DECLARE done INT DEFAULT 0;
    DECLARE order_item_id INT;
    DECLARE order_product_id INT;
    DECLARE order_variant_id INT;
    DECLARE order_quantity INT;

    -- Cursor để lấy tất cả các item trong đơn hàng đã bị hủy
    DECLARE order_cursor CURSOR FOR 
        SELECT oi.order_item_id, oi.product_id, oi.variant_id, oi.quantity
        FROM Order_Items oi
        WHERE oi.order_id = NEW.order_id;

    -- Handler để thoát khỏi cursor
    DECLARE CONTINUE HANDLER FOR NOT FOUND SET done = 1;

    -- Kiểm tra nếu trạng thái chuyển thành 'Cancelled'
    IF NEW.status = 'Cancelled' AND OLD.status <> 'Cancelled' THEN

        OPEN order_cursor;

        read_loop: LOOP
            FETCH order_cursor INTO order_item_id, order_product_id, order_variant_id, order_quantity;

            IF done THEN
                LEAVE read_loop;
            END IF;

            UPDATE Product_Variants
            SET stock_quantity = stock_quantity + order_quantity
            WHERE variant_id = order_variant_id;

        END LOOP;

        CLOSE order_cursor;

    END IF;
END */;;
DELIMITER ;
