/*!50003 CREATE*/ /*!50017 DEFINER=`root`@`localhost`*/ /*!50003 TRIGGER `trigger_before_delete_product` BEFORE DELETE ON `products` FOR EACH ROW BEGIN
    -- Xóa tất cả mục trong giỏ hàng liên quan đến sản phẩm
    DELETE FROM Cart WHERE product_id = OLD.product_id;
    
    -- Xóa tất cả order items liên quan đến sản phẩm
    DELETE FROM Order_Items WHERE product_id = OLD.product_id;
    
    -- Xóa tất cả product specifications liên quan đến sản phẩm
    DELETE FROM Product_Specifications WHERE product_id = OLD.product_id;
    
    -- Xóa tất cả product images liên quan đến sản phẩm
    DELETE FROM Product_Images WHERE product_id = OLD.product_id;
    
    -- Xóa tất cả variant attribute values liên quan đến các biến thể của sản phẩm
    DELETE FROM Variant_Attribute_Values 
    WHERE variant_id IN (SELECT variant_id FROM Product_Variants WHERE product_id = OLD.product_id);
    
    -- Xóa tất cả product variants liên quan đến sản phẩm
    DELETE FROM Product_Variants WHERE product_id = OLD.product_id;
    
END */;;
DELIMITER ;
