/*!50003 DROP PROCEDURE IF EXISTS `AddToCart` */;
/*!50003 SET @saved_cs_client      = @@character_set_client */ ;
/*!50003 SET @saved_cs_results     = @@character_set_results */ ;
/*!50003 SET @saved_col_connection = @@collation_connection */ ;
/*!50003 SET character_set_client  = utf8mb4 */ ;
/*!50003 SET character_set_results = utf8mb4 */ ;
/*!50003 SET collation_connection  = utf8mb4_0900_ai_ci */ ;
/*!50003 SET @saved_sql_mode       = @@sql_mode */ ;
/*!50003 SET sql_mode              = 'ONLY_FULL_GROUP_BY,STRICT_TRANS_TABLES,NO_ZERO_IN_DATE,NO_ZERO_DATE,ERROR_FOR_DIVISION_BY_ZERO,NO_ENGINE_SUBSTITUTION' */ ;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `AddToCart`(
    IN p_user_id INT,
    IN p_product_id INT,
    IN p_quantity INT,
    OUT p_result VARCHAR(100)
)
BEGIN
    DECLARE v_stock INT;
    DECLARE v_existing_quantity INT DEFAULT 0;
    DECLARE v_variant_id INT;
    
    -- Kiểm tra tồn kho
    SELECT variant_id, stock_quantity INTO v_variant_id, v_stock
    FROM Product_Variants
    WHERE product_id = p_product_id AND is_default = TRUE
    LIMIT 1;
    
    IF v_variant_id IS NULL THEN
        SET p_result = 'Sản phẩm không tồn tại';
        SIGNAL SQLSTATE '45000'
        SET MESSAGE_TEXT = 'Sản phẩm không tồn tại hoặc không có biến thể mặc định';
    END IF;
    
    -- Kiểm tra xem sản phẩm đã có trong giỏ hàng chưa
    SELECT COALESCE(SUM(quantity), 0) INTO v_existing_quantity
    FROM Cart
    WHERE user_id = p_user_id AND product_id = p_product_id AND is_active = TRUE;
    
    -- Kiểm tra nếu tổng số lượng vượt quá tồn kho
    IF (v_existing_quantity + p_quantity) > v_stock THEN
        SET p_result = CONCAT('Số lượng tồn kho không đủ. Hiện chỉ còn ', v_stock, ' sản phẩm');
        SIGNAL SQLSTATE '45000'
        SET MESSAGE_TEXT = p_result;
    END IF;
    
    -- Nếu sản phẩm đã có trong giỏ hàng, tăng số lượng
    IF v_existing_quantity > 0 THEN
        UPDATE Cart
        SET quantity = quantity + p_quantity
        WHERE user_id = p_user_id AND product_id = p_product_id AND is_active = TRUE;
        
        SET p_result = 'Đã cập nhật số lượng sản phẩm trong giỏ hàng';
    ELSE
        -- Nếu chưa có, thêm mới vào giỏ hàng
        INSERT INTO Cart(user_id, product_id, quantity)
        VALUES(p_user_id, p_product_id, p_quantity);
        
        SET p_result = 'Đã thêm sản phẩm vào giỏ hàng';
    END IF;
END ;;
DELIMITER ;
