    IN p_shipping_address TEXT,
    IN p_payment_method_id INT,
    IN p_products JSON, -- JSON array chứa sản phẩm: [{product_id, quantity}, ...]
    OUT p_order_id INT
)
BEGIN
    DECLARE v_total DECIMAL(20,2) DEFAULT 0;
    DECLARE v_index INT DEFAULT 0;
    DECLARE v_count INT;
    DECLARE v_product_id INT;
    DECLARE v_quantity INT;
    DECLARE v_price DECIMAL(20,2);
    
    -- Tạo đơn hàng mới với tổng tiền tạm thời = 0
    INSERT INTO Orders(guest_name, guest_email, guest_phone, total_amount, shipping_address, payment_method_id)
    VALUES(p_guest_name, p_guest_email, p_guest_phone, 0, p_shipping_address, p_payment_method_id);
    
    SET p_order_id = LAST_INSERT_ID();
    
    -- Xác định số lượng sản phẩm trong JSON array
    SET v_count = JSON_LENGTH(p_products);
    
    -- Duyệt qua từng sản phẩm
    WHILE v_index < v_count DO
        -- Lấy thông tin sản phẩm từ JSON
        SET v_product_id = JSON_EXTRACT(p_products, CONCAT('$[', v_index, '].product_id'));
        SET v_quantity = JSON_EXTRACT(p_products, CONCAT('$[', v_index, '].quantity'));
        
        -- Lấy giá sản phẩm
        SELECT (p.base_price + COALESCE(pv.price_adjustment, 0)) INTO v_price
        FROM Products p
        LEFT JOIN Product_Variants pv ON p.product_id = pv.product_id AND pv.is_default = TRUE
        WHERE p.product_id = v_product_id;
        
        -- Thêm sản phẩm vào đơn hàng
        INSERT INTO Order_Items(order_id, product_id, quantity, price_at_time)
        VALUES(p_order_id, v_product_id, v_quantity, v_price);
        
        -- Cập nhật tổng tiền
        SET v_total = v_total + (v_price * v_quantity);
        SET v_index = v_index + 1;
    END WHILE;
    
    -- Cập nhật tổng tiền đơn hàng
    UPDATE Orders SET total_amount = v_total WHERE order_id = p_order_id;
END ;;
DELIMITER ;
/*!50003 SET sql_mode              = @saved_sql_mode */ ;
/*!50003 SET character_set_client  = @saved_cs_client */ ;
/*!50003 SET character_set_results = @saved_cs_results */ ;
/*!50003 SET collation_connection  = @saved_col_connection */ ;
/*!50003 DROP PROCEDURE IF EXISTS `CreateOrderForUser` */;
