CREATE DEFINER=`root`@`localhost` PROCEDURE `CreateOrderForUser`(
    IN p_user_id INT,
    IN p_shipping_address TEXT,
    IN p_payment_method_id INT,
    OUT p_order_id INT
)
BEGIN
    DECLARE v_total DECIMAL(20,2) DEFAULT 0;
    
    -- Tính tổng giá trị giỏ hàng
    SELECT SUM(c.quantity * (p.base_price + COALESCE(pv.price_adjustment, 0))) INTO v_total
    FROM Cart c
    JOIN Products p ON c.product_id = p.product_id
    LEFT JOIN Product_Variants pv ON p.product_id = pv.product_id AND pv.is_default = TRUE
    WHERE c.user_id = p_user_id AND c.is_active = TRUE;
    
    -- Tạo đơn hàng mới
    INSERT INTO Orders(user_id, total_amount, shipping_address, payment_method_id)
    VALUES(p_user_id, v_total, p_shipping_address, p_payment_method_id);
    
    SET p_order_id = LAST_INSERT_ID();
    
    -- Thêm các sản phẩm từ giỏ hàng vào đơn hàng
    INSERT INTO Order_Items(order_id, product_id, quantity, price_at_time)
    SELECT p_order_id, c.product_id, c.quantity, (p.base_price + COALESCE(pv.price_adjustment, 0))
    FROM Cart c
    JOIN Products p ON c.product_id = p.product_id
    LEFT JOIN Product_Variants pv ON p.product_id = pv.product_id AND pv.is_default = TRUE
    WHERE c.user_id = p_user_id AND c.is_active = TRUE;
    
    -- Xóa giỏ hàng (đã có trigger xử lý điều này)
END ;;
DELIMITER ;
