CREATE DEFINER=`root`@`localhost` PROCEDURE `GetOrderDetails`(
    IN p_order_id INT
)
BEGIN
    -- Thông tin cơ bản về đơn hàng
    SELECT o.*, pm.payment_method_name
    FROM Orders o
    LEFT JOIN Payment_Methods pm ON o.payment_method_id = pm.payment_method_id
    WHERE o.order_id = p_order_id;
    
    -- Thông tin về các sản phẩm trong đơn hàng
    SELECT oi.*, p.product_name, p.brand_name, p.model, 
           pv.variant_sku, pv.image_url
    FROM Order_Items oi
    JOIN Products p ON oi.product_id = p.product_id
    LEFT JOIN Product_Variants pv ON p.product_id = pv.product_id AND pv.is_default = TRUE
    WHERE oi.order_id = p_order_id;
    
    -- Lịch sử trạng thái đơn hàng
    SELECT osh.*, u.username AS changed_by_username
    FROM Order_Status_History osh
    LEFT JOIN Users u ON osh.changed_by = u.user_id
    WHERE osh.order_id = p_order_id
    ORDER BY osh.changed_at;
END ;;
DELIMITER ;
/*!50003 SET sql_mode              = @saved_sql_mode */ ;
