CREATE DEFINER=`root`@`localhost` PROCEDURE `GetOrderItems`(IN order_id INT)
BEGIN
    -- Truy vấn lấy thông tin order_items của một đơn hàng
    SELECT OI.*, P.product_name, P.model, P.base_price
    FROM order_items AS OI
    JOIN products AS P ON OI.product_id = P.product_id
    WHERE OI.order_id = order_id;
END ;;
