DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `GetOrdersByStatusGroup`(IN status_group VARCHAR(20))
BEGIN
    -- Kiểm tra nhóm trạng thái
    IF status_group = 'Pending' THEN
        SELECT * FROM Orders WHERE status IN ('Pending', 'Processing');
    ELSEIF status_group = 'Completed' THEN
        SELECT * FROM Orders WHERE status IN ('Shipped', 'Delivered');
    ELSEIF status_group = 'Cancelled' THEN
        SELECT * FROM Orders WHERE status = 'Cancelled';
    ELSE
        SELECT 'Invalid group name. Please use "Pending", "Completed", or "Cancelled".' AS message;
    END IF;
END ;;
DELIMITER ;
/*!50003 SET sql_mode              = @saved_sql_mode */ ;
