/*!50003 SET collation_connection  = utf8mb4_0900_ai_ci */ ;
/*!50003 SET @saved_sql_mode       = @@sql_mode */ ;
/*!50003 SET sql_mode              = 'ONLY_FULL_GROUP_BY,STRICT_TRANS_TABLES,NO_ZERO_IN_DATE,NO_ZERO_DATE,ERROR_FOR_DIVISION_BY_ZERO,NO_ENGINE_SUBSTITUTION' */ ;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `GetRevenueSummary`(
    IN p_start_date DATE,
    IN p_end_date DATE,
    IN p_group_by VARCHAR(10) -- 'day', 'month', 'year'
)
BEGIN
    CASE p_group_by
        WHEN 'day' THEN
            SELECT 
                DATE(order_date) AS date,
                COUNT(order_id) AS order_count,
                SUM(total_amount) AS revenue
            FROM Orders
            WHERE order_date BETWEEN p_start_date AND p_end_date
                AND status != 'Cancelled'
            GROUP BY DATE(order_date)
            ORDER BY date;
        
        WHEN 'month' THEN
            SELECT 
                CONCAT(YEAR(order_date), '-', MONTH(order_date)) AS month,
                COUNT(order_id) AS order_count,
                SUM(total_amount) AS revenue
            FROM Orders
            WHERE order_date BETWEEN p_start_date AND p_end_date
                AND status != 'Cancelled'
            GROUP BY YEAR(order_date), MONTH(order_date)
            ORDER BY YEAR(order_date), MONTH(order_date);
        
        WHEN 'year' THEN
            SELECT 
                YEAR(order_date) AS year,
                COUNT(order_id) AS order_count,
                SUM(total_amount) AS revenue
            FROM Orders
            WHERE order_date BETWEEN p_start_date AND p_end_date
                AND status != 'Cancelled'
            GROUP BY YEAR(order_date)
            ORDER BY year;
        
        ELSE
            SIGNAL SQLSTATE '45000'
            SET MESSAGE_TEXT = 'Tham số không hợp lệ. Vui lòng chọn "day", "month" hoặc "year"';
    END CASE;
END ;;
DELIMITER ;
/*!50003 SET sql_mode              = @saved_sql_mode */ ;
/*!50003 SET character_set_client  = @saved_cs_client */ ;
/*!50003 SET character_set_results = @saved_cs_results */ ;
/*!50003 SET collation_connection  = @saved_col_connection */ ;
