/*!50003 SET collation_connection  = utf8mb4_0900_ai_ci */ ;
/*!50003 SET @saved_sql_mode       = @@sql_mode */ ;
/*!50003 SET sql_mode              = 'ONLY_FULL_GROUP_BY,STRICT_TRANS_TABLES,NO_ZERO_IN_DATE,NO_ZERO_DATE,ERROR_FOR_DIVISION_BY_ZERO,NO_ENGINE_SUBSTITUTION' */ ;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `GetUserOrders`(
    IN p_user_id INT
)
BEGIN
    SELECT 
        o.order_id,
        o.user_id,
        o.total_amount,
        o.order_date,
        o.status,
        o.guest_email,
        o.guest_phone,
        o.guest_name,
        o.shipping_address,
        o.payment_status,
        pm.payment_method_name,
        oi.quantity AS quantity,                -- Số lượng mua của sản phẩm
        oi.price_at_time AS price,              -- Giá của sản phẩm tại thời điểm mua
        oi.variant_sku AS variant_sku,          -- Mã biến thể
        pv.variant_id AS variant_id,        
        p.product_name,                         -- Tên sản phẩm
        p.model,                               -- Model sản phẩm
        p.brand_name,                          -- Hãng sản xuất
        (SELECT image_url 
         FROM Product_Variants pv2
         WHERE pv2.product_id = oi.product_id 
         AND pv2.is_default = TRUE
         LIMIT 1) AS product_image             -- Hình ảnh mặc định của sản phẩm
    FROM Orders o
    JOIN Order_Items oi ON o.order_id = oi.order_id
    JOIN Products p ON oi.product_id = p.product_id
    LEFT JOIN Payment_Methods pm ON o.payment_method_id = pm.payment_method_id
    LEFT JOIN Product_Variants pv ON oi.variant_id = pv.variant_id  -- Lấy thông tin về biến thể
    WHERE o.user_id = p_user_id
    ORDER BY o.order_date DESC, o.order_id, oi.order_item_id;          -- Sắp xếp theo ngày đơn hàng, ID đơn hàng, và ID sản phẩm
END ;;
DELIMITER ;
/*!50003 SET sql_mode              = @saved_sql_mode */ ;
