/*!50003 DROP PROCEDURE IF EXISTS `UpdateCartItemQuantity` */;
/*!50003 SET @saved_cs_client      = @@character_set_client */ ;
/*!50003 SET @saved_cs_results     = @@character_set_results */ ;
/*!50003 SET @saved_col_connection = @@collation_connection */ ;
/*!50003 SET character_set_client  = utf8mb4 */ ;
/*!50003 SET character_set_results = utf8mb4 */ ;
/*!50003 SET collation_connection  = utf8mb4_0900_ai_ci */ ;
/*!50003 SET @saved_sql_mode       = @@sql_mode */ ;
/*!50003 SET sql_mode              = 'ONLY_FULL_GROUP_BY,STRICT_TRANS_TABLES,NO_ZERO_IN_DATE,NO_ZERO_DATE,ERROR_FOR_DIVISION_BY_ZERO,NO_ENGINE_SUBSTITUTION' */ ;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `UpdateCartItemQuantity`(
    IN p_user_id INT,
    IN p_product_id INT,
    IN p_quantity INT,
    OUT p_result VARCHAR(100)
)
BEGIN
    DECLARE v_stock INT;
    DECLARE v_cart_exists BOOLEAN DEFAULT FALSE;
    
    -- Kiểm tra sản phẩm có trong giỏ hàng không
    SELECT COUNT(*) > 0 INTO v_cart_exists
    FROM Cart
    WHERE user_id = p_user_id AND product_id = p_product_id AND is_active = TRUE;
    
    IF NOT v_cart_exists THEN
        SET p_result = 'Sản phẩm không có trong giỏ hàng';
        SIGNAL SQLSTATE '45000'
        SET MESSAGE_TEXT = p_result;
    END IF;
    
    -- Nếu số lượng = 0, xóa khỏi giỏ hàng
    IF p_quantity = 0 THEN
        DELETE FROM Cart
        WHERE user_id = p_user_id AND product_id = p_product_id AND is_active = TRUE;
        
        SET p_result = 'Đã xóa sản phẩm khỏi giỏ hàng';
    ELSE
        -- Kiểm tra tồn kho
        SELECT stock_quantity INTO v_stock
        FROM Product_Variants
        WHERE product_id = p_product_id AND is_default = TRUE
        LIMIT 1;
        
        IF p_quantity > v_stock THEN
            SET p_result = CONCAT('Số lượng tồn kho không đủ. Hiện chỉ còn ', v_stock, ' sản phẩm');
            SIGNAL SQLSTATE '45000'
            SET MESSAGE_TEXT = p_result;
        ELSE
            -- Cập nhật số lượng
            UPDATE Cart
            SET quantity = p_quantity
            WHERE user_id = p_user_id AND product_id = p_product_id AND is_active = TRUE;
            
            SET p_result = 'Đã cập nhật số lượng sản phẩm';
        END IF;
    END IF;
END ;;
DELIMITER ;
/*!50003 SET sql_mode              = @saved_sql_mode */ ;
/*!50003 SET character_set_client  = @saved_cs_client */ ;
/*!50003 SET character_set_results = @saved_cs_results */ ;
/*!50003 SET collation_connection  = @saved_col_connection */ ;
/*!50003 DROP PROCEDURE IF EXISTS `UpdateOrderStatus` */;
/*!50003 SET @saved_cs_client      = @@character_set_client */ ;
/*!50003 SET @saved_cs_results     = @@character_set_results */ ;
/*!50003 SET @saved_col_connection = @@collation_connection */ ;
/*!50003 SET character_set_client  = utf8mb4 */ ;
/*!50003 SET character_set_results = utf8mb4 */ ;
/*!50003 SET collation_connection  = utf8mb4_0900_ai_ci */ ;
/*!50003 SET @saved_sql_mode       = @@sql_mode */ ;
/*!50003 SET sql_mode              = 'ONLY_FULL_GROUP_BY,STRICT_TRANS_TABLES,NO_ZERO_IN_DATE,NO_ZERO_DATE,ERROR_FOR_DIVISION_BY_ZERO,NO_ENGINE_SUBSTITUTION' */ ;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `UpdateOrderStatus`(
    IN p_order_id INT,
    IN p_new_status VARCHAR(20),
    IN p_updated_by INT -- user_id của nhân viên thực hiện
)
BEGIN
    DECLARE v_current_status VARCHAR(20);
    
    -- Lấy trạng thái hiện tại
    SELECT status INTO v_current_status FROM Orders WHERE order_id = p_order_id;
    
    -- Kiểm tra logic chuyển trạng thái
    CASE p_new_status
        WHEN 'Processing' THEN
            IF v_current_status <> 'Pending' THEN
                SIGNAL SQLSTATE '45000'
                SET MESSAGE_TEXT = 'Chỉ có thể chuyển từ Pending sang Processing';
            END IF;
        WHEN 'Shipped' THEN
            IF v_current_status <> 'Processing' THEN
                SIGNAL SQLSTATE '45000'
                SET MESSAGE_TEXT = 'Chỉ có thể chuyển từ Processing sang Shipped';
            END IF;
        WHEN 'Delivered' THEN
            IF v_current_status <> 'Shipped' THEN
                SIGNAL SQLSTATE '45000'
                SET MESSAGE_TEXT = 'Chỉ có thể chuyển từ Shipped sang Delivered';
            END IF;
        WHEN 'Cancelled' THEN
            IF v_current_status = 'Delivered' THEN
                SIGNAL SQLSTATE '45000'
                SET MESSAGE_TEXT = 'Không thể hủy đơn hàng đã giao';
            END IF;
        ELSE
            SIGNAL SQLSTATE '45000'
            SET MESSAGE_TEXT = 'Trạng thái không hợp lệ';
    END CASE;
    
    -- Cập nhật trạng thái
    UPDATE Orders SET status = p_new_status WHERE order_id = p_order_id;
    
    -- Ghi nhận người cập nhật vào lịch sử
    INSERT INTO Order_Status_History(order_id, old_status, new_status, changed_by)
	VALUES(p_order_id, v_current_status, p_new_status, p_updated_by);
    
    -- Xử lý hoàn trả hàng tồn kho nếu đơn hàng bị hủy
    IF p_new_status = 'Cancelled' THEN
        -- Hoàn trả số lượng vào kho
        UPDATE Product_Variants pv
        JOIN Products p ON pv.product_id = p.product_id
        JOIN Order_Items oi ON oi.product_id = p.product_id
        SET pv.stock_quantity = pv.stock_quantity + oi.quantity
        WHERE oi.order_id = p_order_id AND pv.is_default = TRUE;
    END IF;
END ;;
